        fetch('http://localhost:8000/artists/${id}')
            .then(res => {
                if (!res.ok) {
                    throw Error("error in fetching data");
                }
                return res.json();
            })
            .then(data => {
                setSinger(data);
                console.log((data));

            })
            .catch(error => {
                setError(error.message)///////
