import {NavLink, useParams} from "react-router-dom"
import {useEffect, useState} from "react";

const ShowArtist = () => {

    const {id} = useParams();////////////////////////////////////////////////////////


    const [singer,setSinger]=useState([]);
    const [error, setError] = useState("")
    const url = "";
    useEffect(  ()=>{

        fetch(`http://localhost:8000/artists/${id}`)
            .then(res => {
                if (!res.ok) {
                    throw Error("error in fetching data");
                }
                return res.json();
            })
            .then(data => {
                setSinger(data);
                console.log((data));

            })
            .catch(error => {
                setError(error.message)///////
            })


    },[]);




    return (
        <div>
            <div>
                {error && <div>{error}</div>}
            </div>
            <div className="row ">
                <table className=" table table-striped table-bordered">
                    <thead>
                    <tr>
                        <th className="align-center" scope="col">Name</th>
                        <th className="align-center" scope="col">cover</th>
                        <th  scope="col">Image</th>
                        <th>Operations</th>
                    </tr>
                    </thead>
                    <tbody>
                    {singer.length > 0 && (
                        singer.map(singerdata =>
                            <tr key={singerdata.id}>
                                <td   className="">{album.name} </td>
                                <td   className="">{album.cover}</td>
                                <td><img src={`/images/covers/${album.cover}.jpg`}/></td>
                                <td>
                                    <NavLink className="btn-primary align-center" type="button" to={`/Artist/${album.id}`} end>
                                        show
                                    </NavLink>
                                </td>
                            </tr>
                        )
                    )}
                    </tbody>
                </table>
            </div>

        </div>
    );
}
export default ShowArtist;
